import { Container, Row, Form, Button, Table } from "react-bootstrap"
import { Typeahead } from 'react-bootstrap-typeahead'
import { useState } from "react";
import { FaPlusCircle } from 'react-icons/fa';

import 'react-bootstrap-typeahead/css/Typeahead.css'

export default function Order() {

    const [selected, setSelected] = useState([]);
    const [productAdded, setProductAdded] = useState([]);
    const products = [
        {
            id: 1,
            name: "Name product",
            price: 20,
            qty_stock: 10
        }
    ]



    const listProductsAdded = 


        // if (productAdded.length > 0) {
            // let htmlTableProducts = `
            //     <Table striped bordered hover size="sm">
            //         <thead>
            //             <tr>
            //                 <th>Produto</th>
            //                 <th>Quantidade</th>
            //             </tr>
            //         </thead>
            //     <tbody>

            productAdded.map(product => {
                return (
                    <tr>
                        <td>${product.name}</td>
                        <td>${product.qty}</td>
                    </tr>
                )

            })

            // htmlTableProducts = htmlTableProducts + `
            //     </tbody>
            //     </Table>
            // `

        // }


    return (
        <Container fluid>
            <Row>
                <Form>
                    <Form.Group className="mb-3" controlId="formBasicClientName">
                        <Form.Label>Nome cliente: </Form.Label>
                        <Form.Control type="text" placeholder="Insira o nome do cliente"></Form.Control>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicDeliveryDate">
                        <Form.Label>Data de entrega: </Form.Label>
                        <Form.Control type="date" placeholder="Insira data de entrega"></Form.Control>
                    </Form.Group>
                    <Form.Group controlId="formBasicAddProducts">
                        <Form.Label>Adicionar produtos: </Form.Label>
                    </Form.Group>
                    <Form.Group>
                        <Typeahead
                            className="w-75"
                            id="products"
                            labelKey="name"
                            onChange={setSelected}
                            options={products}
                            placeholder="Escolha um produto..."
                            selected={selected}
                        />
                        <Form.Control className="inp-qty" type="text" placeholder="Quantidade"></Form.Control>
                        <Button variant="light">
                            <FaPlusCircle />
                        </Button>
                    </Form.Group>
                </Form>
            </Row>

            <Row className="row-table-products">
                <h5>Produtos adicionados no pedido</h5>
            </Row>
            <Row>
                <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>Produto</th>
                                <th>Quantidade</th>
                            </tr>
                        </thead>
                    <tbody>
                    {listProductsAdded}
                </tbody>
                </Table>
            </Row>
        </Container>
    )

}
